                    <p className="watch" onClick={handlePlayVideo}><span className="play-video" id="play"><FontAwesomeIcon icon={faPlay} /></span> Watch Our Story</p>
                    {isVideoPlaying && (
                    <div className="video-player">
                        <iframe
                        width="560"
